mapper.select:
SELECT * FROM ( SELECT boardType, freeNum as num, freeSubject as subject, freeCreated as created, userId FROM comFree UNION ALL SELECT boardType, dealNum as num, dealSubject as subject, dealCreated as created, userId FROM comDeal UNION ALL SELECT boardType, photoNum as num, photoSubject as subject, photoCreated as created, userId FROM comPhoto UNION ALL SELECT boardType, vidNum as num, vidSubject as subject, vidCreated as created, userId FROM vetVideo )
AND boardType=#{boardType, jdbcType=VARCHAR}
ORDER BY created DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT * FROM( SELECT boardType, r.freeNum AS num, freeReplyNum AS replyNum, r.userId, freeReplyCreated AS created, freeReplyContent AS content FROM comFreeReply r JOIN comFree f ON f.freeNum=r.freeNum UNION ALL SELECT boardType, r.dealNum AS num, dealReplyNum AS replyNum, r.userId, dealReplyCreated AS created, dealReplyContent AS content FROM comDealReply r JOIN comDeal d ON d.dealNum=r.dealNum UNION ALL SELECT boardType, r.photoNum AS num, photoReplyNum AS replyNum, r.userId, photoReplyCreated AS created, photoReplyContent AS content FROM comPhotoReply r JOIN comPhoto p ON p.photoNum=r.photoNum UNION ALL SELECT boardType, r.vidNum AS num, vidReplyNum AS replyNum, r.userId, vidReplyCreated AS created, vidReplyContent AS content FROM vetVidReply r JOIN vetVideo v ON v.vidNum=r.vidNum )
AND boardType=#{boardType, jdbcType=VARCHAR}
ORDER BY created DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT num, boardType, articleNum, replyNum, userId, reportCode, memo, report_date, report_id FROM replyReport
AND boardType=#{boardType, jdbcType=VARCHAR}
ORDER BY report_date DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT * FROM ( SELECT r.num, r.boardType, articleNum, r.replyNum, r.userId, reportCode, memo, report_date, report_id, freeReplyContent AS content, freeReplyCreated AS created FROM replyReport r JOIN comFreeReply f ON f.freeReplyNum=r.replyNum UNION ALL SELECT r.num, r.boardType, articleNum, r.replyNum, r.userId, reportCode, memo, report_date, report_id, dealReplyContent AS content, dealReplyCreated AS created FROM replyReport r JOIN comdealReply f ON f.dealReplyNum=r.replyNum
UNION ALL SELECT r.num, r.boardType, articleNum, r.replyNum, r.userId, reportCode, memo, report_date, report_id, photoReplyContent AS content, photoReplyCreated AS created FROM replyReport r JOIN comPhotoReply f ON f.photoReplyNum=r.replyNum
UNION ALL SELECT r.num, r.boardType, articleNum, r.replyNum, r.userId, reportCode, memo, report_date, report_id, vidReplyContent AS content, vidReplyCreated AS created FROM replyReport r JOIN vetVidReply f ON f.vidReplyNum=r.replyNum ) WHERE num=#{num}
