mapper.select:
SELECT COUNT(*) FROM member1 m1 JOIN comMember c ON c.userId=m1.userId
AND m1.userEnabled = #{userEnabled}
AND m1.userId != 'admin'

SELECT COUNT(*) FROM member1 m1 JOIN vetMember v ON v.userId=m1.userId
AND m1.userEnabled = #{userEnabled}
AND m1.userId != 'admin'

SELECT COUNT(*) FROM member1 m1 JOIN sellMember s ON s.userId=m1.userId
AND m1.userEnabled = #{userEnabled}
AND m1.userId != 'admin'
