mapper.select:
SELECT TO_CHAR(pointCreated, 'YYYY-MM-DD') pointCreated, pointContent, pointType, pointQuantity 
FROM comPoint

	userId=#{userId}
	
		AND pointType=#{sortType}
	
	
		AND pointCreated <= #{toDate} AND pointCreated >= #{fromDate}
	

ORDER BY pointCreated DESC
OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT NVL(COUNT(*), 0)
FROM comPoint p

	userId=#{userId}
	
		AND pointType=#{sortType}
	
	
		AND pointCreated <= #{toDate} AND pointCreated >= #{fromDate}

SELECT * FROM ( SELECT boardType, freeNum num, freeSubject subject, TO_CHAR(freeCreated, 'YYYY-MM-DD') created, freeHitCount hitcount, userId FROM comFree UNION ALL SELECT boardType, dealNum num, dealSubject subject, TO_CHAR(dealCreated, 'YYYY-MM-DD') created, dealHitCount hitcount, userId FROM comDeal UNION ALL SELECT boardType, photoNum num, photoSubject subject, TO_CHAR(photoCreated, 'YYYY-MM-DD') created, photoHitCount hitcount, userId FROM comPhoto UNION ALL SELECT boardType, missNum num, missSubject subject, TO_CHAR(missCreated, 'YYYY-MM-DD') created, missHitCount hitcount, userId FROM comMiss )
boardType=#{boardType, jdbcType=VARCHAR}
AND userId=#{userId}
ORDER BY created DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY

SELECT COUNT(boardType) FROM ( SELECT boardType, freeNum num, freeSubject subject, TO_CHAR(freeCreated, 'YYYY-MM-DD') created, freeHitCount hitcount, userId FROM comFree UNION ALL SELECT boardType, dealNum num, dealSubject subject, TO_CHAR(dealCreated, 'YYYY-MM-DD') created, dealHitCount hitcount, userId FROM comDeal UNION ALL SELECT boardType, photoNum num, photoSubject subject, TO_CHAR(photoCreated, 'YYYY-MM-DD') created, photoHitCount hitcount, userId FROM comPhoto UNION ALL SELECT boardType, missNum num, missSubject subject, TO_CHAR(missCreated, 'YYYY-MM-DD') created, missHitCount hitcount, userId FROM comMiss )
AND boardType=#{boardType, jdbcType=VARCHAR}
AND userId=#{userId}
ORDER BY created DESC
