mapper.select:
SELECT NVL(COUNT(*),0) 
FROM product 

	
		
	
	
		AND TO_CHAR(prdCreated, 'YYYY-MM-DD') = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
	
	
		AND prdPet = #{prdPet, jdbcType=INTEGER}
	
	
		AND prdState = #{prdState, jdbcType=INTEGER}

SELECT prdNum, prdName, p.prdCategoryNum, prdCategoryName, prdPet, prdState, prdCreated AS created, userId FROM product p JOIN prdCategory c ON c.prdCategoryNum=p.prdCategoryNum
AND prdPet = #{prdPet, jdbcType=INTEGER}
AND prdState = #{prdState, jdbcType=INTEGER}
ORDER BY created DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY
